INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '100', 'Assisted Squats', 'Rest your hands on the back of a chair in front you. Lower yourself till your thighs are parallel with the floor, and come back up, using the chair as a support.','[LINK]', '', 'assisted squats');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '200', 'Deep assisted squats', 'Same as above, but squat through the full range of motion.','[LINK]', '', 'deep assisted squats');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '300', 'Squats', 'Without any help, lower yourself till your thighs are parallel with the floor, and come back up.','[LINK]', '', 'squats');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '400', 'Deep squats', 'Same as above, but squat through the full range of motion.','[LINK]', '', 'deep squats');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '500', 'Bulgarian split squats', 'Rest your back foot on an elevated platform, with the sole pointing up. Keep your weight over your front leg as you lower yourself.','[LINK]', '', 'bulgarian split squats');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '600', 'Beginner shrimp squats (also known as airborne lunges)', 'Lower yourself on one leg until the knee and toes of the leg that was bent under you touch the floor at the same time. This must be a slow controlled movement. Then come back up, trying to lift your knee and toes at the same time.','[LINK]', '', 'beginner shrimps');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '700', 'Assisted one legged squats', 'Performed with a bench or a chair next to you, extend one leg straight in front of you, and lower yourself all the way down, till your butt touches your heel. The heel must remain on the floor however. Help yourself back up by pushing on the chair.','[LINK]', '', 'assisted one legged squat');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '800', 'Balance assisted one legged squats', 'Same as above, but using a door, door frame, or any vertical structure, mainly for support and balance.','[LINK]', '', 'balance assisted one legged squat');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '900', 'Weighted one legged squats', 'This is somewhat counter-intuitive, but weighted one legged squats are actually easier than standard ones, as the object you hold in front of you acts as a counter-weight. You can use dumbbells if you have some, but any heavy object such as a dictionary will do. Start with a 5kg weight and move down in weight progressively in 1 kg increments until you can do unassisted pistols.','[LINK]', '', 'weighted one legged squat');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '1000', 'One-legged squats (a.k.a. pistol squats)', 'Same as above, but without any assitance.','[LINK]', 'https://youtu.be/ZSjxLCKjODc', 'one legged squat');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '1100', 'Renegade pistols', 'Lower yourself on one leg; then switch legs at the bottom (bring the extended leg back next to the other one,into a full squat position), before coming back up. Video demonstration.','[LINK]', 'https://www.youtube.com/watch?v=Yx7-ywBEIQM', 'renegade squat composite');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '1200', 'Intermediate shrimps', 'Same as shrimp squats, but your back foot must come off the floor before you come back up. This variation shifts the emphasis of the exercise to your quadriceps.','[LINK]', '', 'intermediate shrimps');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('squats', '1300', 'Advanced shrimps', 'Hold your back foot in your hand throughout the exercise. Your knee must touch the floor in a controlled manner.','[LINK]', '', 'advanced shrimps');
