INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '100', 'Leg assisted pull ups', 'Place a chair under your pull up bar, and assist yourself with one leg as you pull your body up. The key here is that you control how much help you get from your assisting leg. Try to reduce this assistance over time. This can be done by placing just the tip of your toes on the chair under you.','[LINK]', 'http://www.youtube.com/watch?v=_M8NHO7tb1I', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '200', 'Jackknife pull ups', ' Place a high object under and in front of your pull up bar (a sturdy chair or -even better- a table). Placement is key for jackknife pull ups to be effective: ideally, the table or chair should be high enough for your legs to rest on it at 90 degrees or less to your torso, with your waist bent. Push with your heels into the chair or table and straighten your waist whilst bringing your chin to the bar','[LINK]', 'http://www.youtube.com/watch?v=0AGflL3itws', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '300', 'Eccentric pull ups', 'Jump up in the pull up position with your chin above the bar, then slowly lower yourself down to the count of 5 seconds.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '400', 'Half pull ups (top half)', 'Start in mid range, and pull yourself up through half the range of motion, until your chin clears the bar. Then lower yourself back to mid range (elbows bent at 90 degrees)','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '500', 'Pull ups', 'From a dead hang, pull yourself up till your chin clears the bar','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '600', 'Close grip pull ups', 'Same as a pull up, but with your hands close together. This variation places added emphasis on your biceps.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '700', 'Wide grip pull ups', 'Same as a pull up, but with your hands further than shoulder width apart. This variation places added emphasis on your latissimus dorsi','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '800', 'Archer pull ups', 'Focus on pulling with one arm, with minimal assistance from the other. The other arm should gradually straighten over the bar during the pulling motion. Only go up to 6 on each arm, alternating each arm, rather than the 8 reps the basic routine calls for. This variation of pull ups is key to working your way to a muscle up. If 4 archer pull ups are too much to begin with, start with 3 on each arm.','[LINK]', 'http://www.youtube.com/watch?v=ikKG260TMwU', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '900', 'Sternum pull ups', 'This move shifts the emphasis away from the latissimus dorsi, but it is another key step in the progression towards a muscle up. Perform a pull up until the bar is at chest level. Make sure your elbows remain close to your body throughout the later part of the move, and that they drive back past your shoulders. This move is not to be confused with the gironda sternum pull up, through which your back is arched.','[LINK]', 'http://www.youtube.com/watch?v=-XXuIwW56Zw', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '1000', ' Belly button pull ups', 'Same as above, but pull more explosively until your belly button is level with the bar. Again, your elbows should brush past your sides, and drive back past your shoulders','[LINK]', 'http://www.youtube.com/watch?v=-XXuIwW56Zw', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '1100', 'One arm towel-assisted pull ups', 'The lower your grip is on the towel, the less assistance your other arm will actually provide. Also try to modify your grip on the towel to reduce the assitance (hold with only 2 fingers for instance). Only go up to 6 reps on each arm, alternating each arm, rather than the 8 reps the basic routine calls for.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '1200', 'One arm towel-assisted pull ups and eccentrics', 'Let go of the towel once your chin clears the bar, and lower yourself over a count of 5 seconds. Only go up to 6 reps on each arm, alternating each arm, rather than the 8 reps the basic routine calls for.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '1300', ' Half one arm pull ups (top half)', 'Start in mid range, and pull yourself up through half the range of motion, until your chin clears the bar. Then lower yourself back to mid range (elbows bent at 90 degrees).','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pullups', '1400', 'One arm pull ups', ' From a dead hang, pull yourself up through the whole range of motion.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '100', 'Incline pike push ups (a.k.a. incline military press push ups)', 'Place your hands shoulder-width apart on a raised platform. Bend at the waist keeping a straight back. Your outstretched arms and torso should form a straight line. Bring your head to your hands.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '200', 'Incline pike diamond push ups (a.k.a.incline Chinese push ups)', 'Place your hands with thumbs and index fingers touching each other,  on a raised platform. Bend at the waist keeping a straight back, and bring your head to your hands.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '300', 'Pike push ups (a.k.a. military press push ups)', 'Same as above, without the platform. Make sure that your head travels directly between your arms. Try to keep your head, arms and torso all in the same straight line.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '400', 'Pike diamond push ups (a.k.a.Chinese push ups)', 'Same as above without the platform.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '500', 'Decline pike push ups (a.k.a. decline military press push ups)', 'Same as above with feet on a raised platform, and hands on the floor.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '600', 'Decline pike diamond push ups (a.k.a.decline Chinese push ups)', 'Same as above with feet on a raised platform, and hands on the floor.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '700', 'Wall bent waist handstand push up', 'Walk your feet up the wall, keep a bent waist (you will need to remain some distance from the wall), then perform a push up. Make sure your head and torso remain in line with your arms.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '800', 'Wall bent waist handstand diamond push ups', 'Same as above with hands in diamond position.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '900', 'Wall half handstand push up', 'Facing the wall in a handstand position, lower yourself till your arms are half bent, then come up.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '1000', 'Wall handstand push ups', 'Lower yourself all the way down, until your head touches the ground.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '1100', 'Wall handstand diamond push ups', 'Same as wall handstand push ups, but with thumbs and index fingers touching each other','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('handstand', '1200', 'Wall raised handstand push ups', 'Using parallets, push up handles, or any raised platform such as dictionaries, benches, chairs, etc, to increase the range of motion.<br />For this version of the exercise, you will need to be facing away from the wall, rather than towards it.<br />Gradually build up the height of the platform to increase your range of motion.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '100', 'Flat knee raises', 'Lying on the floor, bring your knees up whilst keeping your legs bent at a 90 degree angle. Make sure to keep breathing throughout the exercise.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '200', 'Flat bent leg raises', 'Lying on the floor, bring your legs vertical with a slight bend in them, then return without touching the floor.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '300', 'Flat straight leg raises', 'Lying on the floor, bring your legs vertical, keeping them straight at all times, then return without touching the floor.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '400', 'Forearm knee raises', 'With your arms resting on parallel bars, raise your knees till they are parallel with the floor, then return your legs to a hanging position.<br />This exercise can also be performed with your arms resting on the back of 2 chairs (you can use 2 towels for cushioning).','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '500', 'Hanging knee raises', 'Same as above but hanging from a pull up bar','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '600', 'Forearm bent leg raises', 'Similar to knee raises but performed with your legs at a 45 degree angle.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '700', 'Hanging bent leg raises', 'As above, but hanging from a pull up bar.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '800', 'Forearm straight leg raises', 'With your arms resting on the back of 2 chairs or on parallel bars, raise your legs straight till they are parallel with the floor, then return.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '900', 'Hanging straight leg raises', 'Same as above but hanging from a pull up bar. ','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '1000', 'Hanging bent leg V-raises', 'Keeping a 45 degree bend in your legs throughout the motion, raise them till your feet are level with your head or higher. Keep your arms straight throughout the exercise.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '1100', 'Hanging straight leg V-raises (a.k.a. toes to bar). ', 'Hanging from a pull up bar, raise your legs straight, till they touch the bar, then lower under control.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '1200', ' Hanging fan raises', 'Bring your toes to the bar in between your hands, then bring them back down. Thats one rep. Now repeat by bringing your toes up past your right hand, back down, then past your left hand and down. Thats 3 reps.','[LINK]', 'https://www.youtube.com/watch?v=JOVlyJ19DJ4', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '1300', 'Hanging V-raise windshield wipers', 'Raise your legs straight untill your toes touch the bar, then twist to one side and the other, before lowering your legs down again. Thats one rep.','[LINK]', 'http://www.youtube.com/watch?v=6ZKZCxAfPE8&feature=youtu.be', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('legraises', '1400', 'One arm hanging leg raises', ' At first, use the other arm for balance, to prevent your body from rotating','[LINK]', 'http://www.youtube.com/watch?v=nDK7Fc_ryck', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '100', 'Wall push ups', 'Performed against a wall. To find the starting position, stand away from the wall and extend your arms in front of you till the tips of your fingers come in contact with the wall. If you find the exercise too hard, come a little closer to the wall.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '200', 'Box push ups', 'Performed on your knees, with your spine and upper legs at a 90 degrees angle.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '300', 'Three quarter push ups', 'On your knees, with your spine and thighs in line.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '400', 'Three quarters push ups + 5s eccentric push up.', '3/4 push ups on the way up, then get on the balls of your feet and perform the lowering phase of a standard push up to the count of 5 seconds.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '500', 'Push ups', 'Your upper arms should form a 45 degree angle with your torso, hands shoulder-width apart. Lower yourself with good form through the whole range of motion untill your chest and your nose almost come into contact with the floor.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '600', 'Elbows in push ups', 'Same as a push up, but with your elbows remaining in contact with your sides.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '700', 'Diamond push ups', 'Thumb touching thumb, and index to index. Your elbows should brush against your sides. These put proportionally more of an emphasis on your triceps.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '800', 'Uneven push ups', 'One hand resting on an object such as a bench, a basket ball, etc. The higher the object, the more the other arm will have to work, and the harder the exercise.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '900', 'Decline push ups', 'Feet on a raised platform.','[LINK]', '', '');
