INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '1200', 'Wall one arm push ups (the wall should be one arms length away from you)', 'Just stand facing the wall and reach forward, until you can touch it with your fingertips, then rest your hand against the wall by leaning forward, and perform a push up','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('pushups', '1300', 'Incline one arm push ups', 'Place one hand under your breastbone, and lower yourself until your chest comes into contact with the bench','[LINK]', '', '');
