INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '100', 'Bent knee bench dips', 'Place your hands flat on a bench or chair behind you, and lower yourself until your butt nearly touches the floor. Lower yourself over the count of 3 seconds. Breathe out as you push yourself back up. ','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '200', 'Straight legs bench dips', 'Same as above but performed with straight legs.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '300', 'One elevated leg, straight legs bench dips', 'Raise one of your legs off the floor.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '400', 'Elevated legs bench dips', '','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '500', 'One extended leg, elevated legs bench dips', '','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '600', 'Legs supported dips', 'Perform these in between on dip bars. Allow your feet to rest on the floor behind you, and try to use minimal assistance from your legs when pushing yourself back up.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '700', 'One leg supported dips', 'Same as above, but reducing the assistance from your legs by using only one leg.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '800', 'Jumping and 5s eccentric dips', 'Jump into the dip position, then lower yourself over 5 seconds. When you reach the bottom position, place your feet on the floor and jump back up. You are merely concerned with the eccentric phase of the movement.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '900', 'Half dips (top half)', 'From the top position, lower yourself till your upper arms are parallel with the floor, then push yourself back up.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '1000', 'Dips', 'Lower yourself between two chairs or parallel bars through the whole range of movement, feet off the floor, then push back to the starting position.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '1100', 'Legs forward dips', ' This movement slightly changes the mechanics of the exercise, but it is a key step in the progression towards a muscle up: once in the top dip position, raise you knees so that your legs would be in front of you, rather than behind you throughout the dipping motion.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '1200', ' Modified Russian dips', 'Having lowered yourself in the legs forward dip position, carry on getting lower until your forarms are parallel with the floor. This might place a bit of strain on your wrists, but it closely mimicks the middle portion of a muscle up. It also forces you to dip lower than you might have done until now.','[LINK]', 'http://www.youtube.com/watch?v=7mB5QbmwIFc', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('dips', '1300', 'Russian dips (a.k.a. chicken dips)', 'These will require a set or parallel bars. From the legs forward dip position, lower yourself all the way until your upper arms rest on the parallel bars. Then pull yourself back up, helping yourself by bringing your head and torso forward','[LINK]', 'http://www.youtube.com/watch?v=qD60OmbUc9M', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('horizontalpulls', '100', 'Vertical pulls (also known as door pulls, or let me ins)', 'Place your feet on both sides of a vertical structure, grab hold of the structure on each side, and lean back. Keep your elbows close to your body as you pull, and squeeze your shoulders together on top of the movement. The closer your feet are to the object (or the further they are past it), the harder the exercise. You should breathe in as you lower yourself, and breathe out as you pull.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('horizontalpulls', '200', ' Vertical pulls with a towel', 'The towel allows you to get lower and makes the exercise harder.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('horizontalpulls', '300', 'Inverted rows legs bent', 'Performed on a low horizontal bar, with bent legs, feet resting on the floor. Lower yourself slowly over 3 seconds. Squeeze your shoulder blades together as you reach the top of the movement to engage your rhomboids.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('horizontalpulls', '400', 'Inverted rows, straight legs', 'Performed on a low horizontal bar, with straight legs, feet resting on the floor.','[LINK]', '', '');
