INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('horizontalpulls', '800', 'Tuck knees, front lever rows', 'Suspended from a pull up bar, bring your knees under the bar and try to keep your upper body parallel to the floor. Then pull with your arms, bringing your chest towards the bar.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('horizontalpulls', '900', 'Avanced tuck knees, front lever rows', 'Bringing your knees in front of the pull up bar not only increases the leverage of the exercise, it also gets them out of the way, thus allowing for a greater range of motion.','[LINK]', '', '');
