INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('plank', '100', 'Kneeling plank', 'Performed on your knees, with thighs and spine in line.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('plank', '200', 'Kneeling side plank', 'Bend your legs at the knee at a right angle and keep your thighs and torso in line. Your forearm, elbow, knee and lower leg are your points of contact with the ground','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('plank', '300', 'Plank', 'With your weight resting on your toes and forearms, keep your core muscles tight and remember to breathe throughout the duration of the exercise.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('plank', '400', 'Side plank', 'Can be performed with your upper arm at your side, or in the air. Aim for 30 seconds on each side, with a quick transition between the two.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('plank', '500', 'Decline plank', 'Performed with feet elevated on a bench. Keep a straight back and remember to breathe throughout the exercise.','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('plank', '600', 'Leg lift plank', 'From the plank position, lift a leg straight behind you, parallel with the floor. Hold one side for up to 30 seconds; then change legs for the remaining 30s','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('plank', '700', 'Arm and leg lift plank', 'From the plank position, lift a leg up and its opposite arm. Keep an horizontal back throughout the exercise. Hold one side for up to 30s; then switch arms and legs for the remaining 30s','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('plank', '800', 'Arm and leg lift side plank', 'From the side plank position, raise your top leg up in the air as well as your top arm. Aim for 30 seconds on each side, with a quick transition between the two','[LINK]', '', '');
INSERT INTO `workout`.`steps` (`categoryName`, `stepID`, `name`, `instructions`,`link`, `video`, `image`) VALUES ('plank', '900', 'Wall plank', 'Performed with your feet flat against a wall. Prop yourself hard on your forearms and dig the soles of your feet hard into the wall','[LINK]', '', '');
